------------------------------------------\n// 脚本方法模块声明\n//-----------------------------------------------------------------\nlocal f_string 	= require(\"util_string\")\n\n//-----------------------------------------------------------------\n// 局部变量声明\n//-----------------------------------------------------------------\nlocal f_name	= \"\"\n\n//-----------------------------------------------------------------\n// 局部函数声明\n//-----------------------------------------------------------------\nlocal V_InitUI\n\n//-----------------------------------------------------------------\n// C++回调函数\n//-----------------------------------------------------------------\nfunction Init(_param)\n	// 注册UI事件	\n	this:RegUIEvent(bt_close, \"Close\", \"bt_close_OnClick()\")\n	\n	// 注册游戏事件\n	this:RegGameEvent(\"OpenWindow\", \"OnEvent_OpenWindow\")\n\n	// 注册网络事件\n	this:RegNetCmd(\"MS_OpenWindow\", OnNet_OpenWindow)	\n\n\n	V_InitUI()			\nend\n\n//-----------------------------------------------------------------\n// 控件事件处理函数\n//-----------------------------------------------------------------\nfunction bt_close_OnClick()\n\nend\n\n//-----------------------------------------------------------------\n// 游戏事件处理函数\n//-----------------------------------------------------------------\nfunction OnEvent_OpenWindow(_name, _event)\n\nend\n\n//-----------------------------------------------------------------\n// 网络消息处理函数\n//-----------------------------------------------------------------\nfunction OnNet_OpenWindow(_name, _cmd)\n\nend\n\n//-----------------------------------------------------------------\n// 局部函数\n//-----------------------------------------------------------------\nfunction V_InitUI()\n\nend\n\n\n\n方法类脚本结构:\n//-----------------------------------------------------------------\n// 脚本方法模块声明\n//-----------------------------------------------------------------\nmodule(\"util_string\", package.seeall)\n\n//-----------------------------------------------------------------\n// 局部变量声明\n//-----------------------------------------------------------------\nlocal f_string = \"\"\n\n//-----------------------------------------------------------------\n// 局部函数声明\n//-----------------------------------------------------------------\nlocal v_get_string\n\n//-----------------------------------------------------------------\n// 接口函数\n//-----------------------------------------------------------------\nfunction parse_token(_string)\n\nend\n\n//-----------------------------------------------------------------\n// 局部函数\n//-----------------------------------------------------------------\nfunction v_get_string()\n	\nend\n\n对象类脚本\n//-----------------------------------------------------------------\n// 脚本方法模块声明\n//-----------------------------------------------------------------\nmodule(\"object_hero\", package.seeall)\n\nlocal f_enum            = require(\"enum_combat\")\n\n//-----------------------------------------------------------------\n// 局部变量声明\n//-----------------------------------------------------------------\nlocal f_hero		= {}\n\n//-----------------------------------------------------------------\n// 局部函数声明\n//-----------------------------------------------------------------\nlocal v_init_spell\n\n//-----------------------------------------------------------------\n// 接口函数\n//-----------------------------------------------------------------\nfunction new_hero(_location, _entry_id, _fashion_id, _scene)\n\nend\n\n//-----------------------------------------------------------------\n// 对象成员函数\n//-----------------------------------------------------------------\nfunction f_hero.attack(_hero, _target)\n\nend\n\n//-----------------------------------------------------------------\n// 局部函数\n//-----------------------------------------------------------------\nfunction v_init_spell()\n\nend\n```','<h1 id=\"cf0cqi\" class=\"markdown-heading\"><a name=\"cf0cqi\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>客户端脚本书写规范</h1><h2 id=\"6knzhc\" class=\"markdown-heading\"><a name=\"6knzhc\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>脚本类型和前缀对应表</h2><h3 id=\"788x3q\" class=\"markdown-heading\"><a name=\"788x3q\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>Frame框架类对应脚本：</h3><table>\n<thead>\n<tr>\n<th>前缀名</th>\n<th>功能</th>\n</tr>\n</thead>\n<tbody><tr>\n<td><code>ui_</code></td>\n<td>界面脚本</td>\n</tr>\n<tr>\n<td><code>scene_</code></td>\n<td>场景脚本</td>\n</tr>\n<tr>\n<td><code>ai_</code></td>\n<td>角色AI脚本</td>\n</tr>\n<tr>\n<td><code>session_</code></td>\n<td>主要注册网络消息的脚本</td>\n</tr>\n<tr>\n<td><code>frame_</code></td>\n<td>普通框架脚本</td>\n</tr>\n</tbody></table>\n<h3 id=\"d2mmjc\" class=\"markdown-heading\"><a name=\"d2mmjc\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>各种方法类脚本</h3><table>\n<thead>\n<tr>\n<th>前缀名</th>\n<th>功能</th>\n</tr>\n</thead>\n<tbody><tr>\n<td><code>util_</code></td>\n<td>方法类脚本</td>\n</tr>\n<tr>\n<td><code>db_</code></td>\n<td>存储游戏数据的脚本</td>\n</tr>\n<tr>\n<td><code>container_</code></td>\n<td>容器类脚本，存储背包英雄等数据的脚本</td>\n</tr>\n<tr>\n<td><code>enum_</code></td>\n<td>枚举类脚本</td>\n</tr>\n</tbody></table>\n<h3 id=\"3opu6h\" class=\"markdown-heading\"><a name=\"3opu6h\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>各种对象模块脚本</h3><table>\n<thead>\n<tr>\n<th>前缀名</th>\n<th>功能</th>\n</tr>\n</thead>\n<tbody><tr>\n<td><code>object_</code></td>\n<td>对象脚本</td>\n</tr>\n<tr>\n<td><code>ctrl_</code></td>\n<td>控制器脚本</td>\n</tr>\n</tbody></table>\n<h2 id=\"22bxx8\" class=\"markdown-heading\"><a name=\"22bxx8\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>脚本函数规则</h2><p class=\"line\">脚本函数和C++接口函数命名要有所区分，方便识别C++脚本接口<br>全局函数以_开头全部小写的方式（为了和原来用法习惯保持不变），如 _createframe</p>\n<p class=\"line\">成员函数以单词首字母大写连写的方式命名，如 <code>GetFrameName</code></p>\n<h3 id=\"98zjfw\" class=\"markdown-heading\"><a name=\"98zjfw\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>Lua全局接口:</h3><ul>\n<li>global_functions.lua中的函数以单词首字母大写连写的方式命名，如 GetEntry</li><li>global_enums.lua中枚举命名以服务器枚举命名规则为准，自己添加的枚举首字母大写</li><li>global_string.lua中的字符串名称首字母大写</li></ul>\n<blockquote class=\"info\">\n<p class=\"line\">三个全局接口加载顺序依次是enums-&gt;strings-&gt;functions，分别对应枚举，字符串和函数，所以每个文件不要有不相符的内容</p>\n</blockquote>\n<h3 id=\"g1xc2e\" class=\"markdown-heading\"><a name=\"g1xc2e\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>Lua框架类脚本</h3><ul>\n<li>C++回调函数以单词首字母大写连写方式命名，如Init</li><li>局部函数以V_开头，单词首字母大写连写方式命名，如V_InitUI</li><li>事件处理函数以OnEvent_开头，如OnEvent_OpenDialog</li><li>网络消息处理函数以OnNet_开头，如OnNet_ChangeName<br>控件事件处理函数以“控件名<em>On操作”，如bt</em>close_OnClick</li></ul>\n<h3 id=\"3x2qsg\" class=\"markdown-heading\"><a name=\"3x2qsg\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>Lua方法类和对象类脚本</h3><ul>\n<li>接口函数采用小写单词用下划线分割的方式，如get_player_info</li><li>局部函数以v_为开头小写单词用下划线分割的方式，如v_get_player_name</li><li>对象成员函数采用小写单词用下划线分割的方式，如get_player_info</li></ul>\n<h2 id=\"1vyec1\" class=\"markdown-heading\"><a name=\"1vyec1\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>变量命名规则</h2><p class=\"line\">变量都已小写单词用下划线分割的方式</p>\n<ul>\n<li>文件级的局部变量以<code>f_</code>开头，如<code>local f_name</code></li><li>函数级的局部变量以<code>l_</code>开头，如<code>local l_name</code></li><li>函数的参数以<code>_</code>为开头，如 <code>function OnEvent(_event)</code></li><li>定义的常量以<code>x_</code>开头，枚举以<code>e_</code>开头</li><li>全局变量只用于常量或者枚举</li></ul>\n<pre class=\"hljs java\"><code>框架类脚本结构:\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 脚本方法模块声明</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\nlocal f_string     = require(<span class=\"hljs-string\">\"util_string\"</span>)\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 局部变量声明</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\nlocal f_name    = <span class=\"hljs-string\">\"\"</span>\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 局部函数声明</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\nlocal V_InitUI\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// C++回调函数</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-function\">function <span class=\"hljs-title\">Init</span><span class=\"hljs-params\">(_param)</span>\n    <span class=\"hljs-comment\">// 注册UI事件    </span>\n    <span class=\"hljs-keyword\">this</span>:<span class=\"hljs-title\">RegUIEvent</span><span class=\"hljs-params\">(bt_close, <span class=\"hljs-string\">\"Close\"</span>, <span class=\"hljs-string\">\"bt_close_OnClick()\"</span>)</span>\n\n    <span class=\"hljs-comment\">// 注册游戏事件</span>\n    <span class=\"hljs-keyword\">this</span>:<span class=\"hljs-title\">RegGameEvent</span><span class=\"hljs-params\">(<span class=\"hljs-string\">\"OpenWindow\"</span>, <span class=\"hljs-string\">\"OnEvent_OpenWindow\"</span>)</span>\n\n    <span class=\"hljs-comment\">// 注册网络事件</span>\n    <span class=\"hljs-keyword\">this</span>:<span class=\"hljs-title\">RegNetCmd</span><span class=\"hljs-params\">(<span class=\"hljs-string\">\"MS_OpenWindow\"</span>, OnNet_OpenWindow)</span>    \n\n\n    <span class=\"hljs-title\">V_InitUI</span><span class=\"hljs-params\">()</span>            \nend\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 控件事件处理函数</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\nfunction <span class=\"hljs-title\">bt_close_OnClick</span><span class=\"hljs-params\">()</span>\n\nend\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 游戏事件处理函数</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\nfunction <span class=\"hljs-title\">OnEvent_OpenWindow</span><span class=\"hljs-params\">(_name, _event)</span>\n\nend\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 网络消息处理函数</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\nfunction <span class=\"hljs-title\">OnNet_OpenWindow</span><span class=\"hljs-params\">(_name, _cmd)</span>\n\nend\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 局部函数</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\nfunction <span class=\"hljs-title\">V_InitUI</span><span class=\"hljs-params\">()</span>\n\nend\n\n\n\n方法类脚本结构:\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 脚本方法模块声明</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-title\">module</span><span class=\"hljs-params\">(<span class=\"hljs-string\">\"util_string\"</span>, <span class=\"hljs-keyword\">package</span>.seeall)</span>\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 局部变量声明</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\nlocal f_string </span>= <span class=\"hljs-string\">\"\"</span>\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 局部函数声明</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\nlocal v_get_string\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 接口函数</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-function\">function <span class=\"hljs-title\">parse_token</span><span class=\"hljs-params\">(_string)</span>\n\nend\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 局部函数</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\nfunction <span class=\"hljs-title\">v_get_string</span><span class=\"hljs-params\">()</span>\n\nend\n\n对象类脚本\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 脚本方法模块声明</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-title\">module</span><span class=\"hljs-params\">(<span class=\"hljs-string\">\"object_hero\"</span>, <span class=\"hljs-keyword\">package</span>.seeall)</span>\n\nlocal f_enum            </span>= require(<span class=\"hljs-string\">\"enum_combat\"</span>)\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 局部变量声明</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\nlocal f_hero        = {}\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 局部函数声明</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\nlocal v_init_spell\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 接口函数</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-function\">function <span class=\"hljs-title\">new_hero</span><span class=\"hljs-params\">(_location, _entry_id, _fashion_id, _scene)</span>\n\nend\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 对象成员函数</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\nfunction f_hero.<span class=\"hljs-title\">attack</span><span class=\"hljs-params\">(_hero, _target)</span>\n\nend\n\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\n<span class=\"hljs-comment\">// 局部函数</span>\n<span class=\"hljs-comment\">//-----------------------------------------------------------------</span>\nfunction <span class=\"hljs-title\">v_init_spell</span><span class=\"hljs-params\">()</span>\n\nend</span></code></pre>',2,'2019-09-17 19:00:58',2,1568718057,0),(63,'modify','修改文档',19,'客户端版本更新说明',0,'','',2,'2019-09-17 20:20:28',2,1568722828,0);
